function traces=adddata2(filenum,tstart,tstop)s=pwd;rootfolder='C:\Documents and Settings\Lab\My Documents\charles';cd ..s2=pwd;cd(s);n=length(s2);filename=strcat(rootfolder,'\',s2(end-6:end),'_',s(n+2:n+3),'_gooddata.mat')switch nargin    case 0    if ~exist(filename)        display('No data in the index');        traces=[];    else        u=load(filename);        traces=u.traces;    end    case 1        if ~exist(filename)    traces=filenum;    save(filename,traces)    else    u=load(filename);    traces=u.traces;    traces=cat(1,traces,filenum);    save(filename,traces)        end    case 3if ~exist(filename)    traces=[filenum,tstart,tstop];    save(filename,traces)else    u=load(filename);    traces=u.traces;    traces(end+1,:)=[filenum,tstart,tstop];(save(filename,traces));end    endend